import React, { useState, useEffect, useRef } from 'react';
import { SearchQuery } from '../../services/api';
import { searchPostalCodes, getCityByPostalCode, getPostalCodeByCity } from '../../services/addressService';
import { saveLastSearch, getLastSearch, getSearchHistory, SearchHistory } from '../../services/sessionService';

interface SearchFormProps {
  onSearch: (query: SearchQuery) => void;
  isLoading: boolean;
}

interface ValidationErrors {
  street?: string;
  houseNumber?: string;
  city?: string;
  postalCode?: string;
}

export const SearchForm: React.FC<SearchFormProps> = ({ onSearch, isLoading }) => {
  const [formData, setFormData] = useState<SearchQuery>({
    street: '',
    houseNumber: '',
    city: '',
    postalCode: ''
  });

  const [validationErrors, setValidationErrors] = useState<ValidationErrors>({});
  const [showValidation, setShowValidation] = useState(false);
  
  // Address autocompletion states
  const [postalSuggestions, setPostalSuggestions] = useState<any[]>([]);
  const [citySuggestions, setCitySuggestions] = useState<any[]>([]);
  const [showPostalSuggestions, setShowPostalSuggestions] = useState(false);
  const [showCitySuggestions, setShowCitySuggestions] = useState(false);
  
  // Session state
  const [searchHistory, setSearchHistory] = useState<SearchHistory[]>([]);
  const [showHistory, setShowHistory] = useState(false);
  
  const postalInputRef = useRef<HTMLInputElement>(null);
  const cityInputRef = useRef<HTMLInputElement>(null);

  // Load last search and history on component mount
  useEffect(() => {
    const lastSearch = getLastSearch();
    if (lastSearch) {
      setFormData(lastSearch);
    }
    setSearchHistory(getSearchHistory());
  }, []);

  const validateField = (name: string, value: string): string | undefined => {
    switch (name) {
      case 'street':
        if (value && /^\d{5}$/.test(value)) {
          return 'This looks like a postal code. Street names should contain letters.';
        }
        if (value && /^[0-9]+$/.test(value)) {
          return 'Street names should contain letters, not just numbers.';
        }
        break;

      case 'houseNumber':
        if (value && /^[xX]+$/.test(value)) {
          return 'Please enter a valid house number (e.g., 123, 45A).';
        }
        if (value && value.length > 0 && !/^[0-9]+[a-zA-Z]?$/.test(value)) {
          return 'House number should be numeric (e.g., 123, 45A).';
        }
        break;

      case 'city':
        if (value && /^\d{5}$/.test(value)) {
          return 'This looks like a postal code. Did you mean to enter a city name?';
        }
        if (value && /^[0-9]+$/.test(value)) {
          return 'City names should contain letters, not just numbers.';
        }
        if (value && value.length > 0 && !/^[a-zA-ZäöüÄÖÜß\s-]+$/.test(value)) {
          return 'City names should only contain letters and spaces.';
        }
        break;

      case 'postalCode':
        if (value && value.length > 0 && !/^\d{5}$/.test(value)) {
          return 'German postal codes should be exactly 5 digits (e.g., 80331).';
        }
        if (value && /^[a-zA-Z]+$/.test(value)) {
          return 'This looks like a city name. Postal codes should be 5 digits.';
        }
        break;
    }
    return undefined;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setShowValidation(true);

    const errors: ValidationErrors = {};
    Object.keys(formData).forEach(key => {
      const error = validateField(key, formData[key as keyof SearchQuery]);
      if (error) {
        errors[key as keyof ValidationErrors] = error;
      }
    });

    setValidationErrors(errors);

    if (Object.keys(errors).length === 0) {
      saveLastSearch(formData);
      setSearchHistory(getSearchHistory());
      onSearch(formData);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });

    // Address autocompletion
    if (name === 'postalCode' && value.length >= 2) {
      const suggestions = searchPostalCodes(value);
      setPostalSuggestions(suggestions);
      setShowPostalSuggestions(suggestions.length > 0);
    } else if (name === 'postalCode') {
      setShowPostalSuggestions(false);
    }

    if (name === 'city' && value.length >= 2) {
      const suggestions = searchPostalCodes(value);
      setCitySuggestions(suggestions);
      setShowCitySuggestions(suggestions.length > 0);
    } else if (name === 'city') {
      setShowCitySuggestions(false);
    }

    // Real-time validation
    if (showValidation) {
      const error = validateField(name, value);
      setValidationErrors(prev => ({
        ...prev,
        [name]: error
      }));
    }
  };

  const handlePostalCodeSelect = (suggestion: any) => {
    setFormData({
      ...formData,
      postalCode: suggestion.postalCode,
      city: suggestion.city
    });
    setShowPostalSuggestions(false);
  };

  const handleCitySelect = (suggestion: any) => {
    setFormData({
      ...formData,
      city: suggestion.city,
      postalCode: suggestion.postalCode
    });
    setShowCitySuggestions(false);
  };

  const handleHistorySelect = (historyItem: SearchHistory) => {
    setFormData(historyItem.query);
    setShowHistory(false);
  };

  // ✅ CORRECTED handleBlur function
  const handleBlur = (e: React.FocusEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    
    // Auto-fill city/postal code if possible
    if (name === 'postalCode' && value.length === 5) {
      const city = getCityByPostalCode(value);
      if (city && !formData.city) {
        setFormData(prev => ({ ...prev, city }));
      }
    }
    
    if (name === 'city' && value && !formData.postalCode) {
      const postalCode = getPostalCodeByCity(value);
      if (postalCode) {
        setFormData(prev => ({ ...prev, postalCode }));
      }
    }

    // Validation
    const error = validateField(name, value);
    setValidationErrors(prev => ({
      ...prev,
      [name]: error
    }));

    // ✅ FIXED: Hide suggestions after a delay to allow clicks
    setTimeout(() => {
      setShowPostalSuggestions(false);     // Hide postal dropdown
      setShowCitySuggestions(false);       // Hide city dropdown
      setPostalSuggestions([]);            // Clear postal data
      setCitySuggestions([]);              // Clear city data
    }, 200);
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-lg">
      <h1 className="text-3xl font-bold text-center mb-6 text-blue-600">
        Internet Provider Comparison
      </h1>

      {/* Search History */}
      {searchHistory.length > 0 && (
        <div className="mb-4">
          <button
            type="button"
            onClick={() => setShowHistory(!showHistory)}
            className="text-sm text-blue-600 hover:underline"
          >
            {showHistory ? 'Hide' : 'Show'} recent searches ({searchHistory.length})
          </button>
          
          {showHistory && (
            <div className="mt-2 bg-gray-50 rounded-md p-3">
              <div className="space-y-2">
                {searchHistory.map((item, index) => (
                  <button
                    key={index}
                    type="button"
                    onClick={() => handleHistorySelect(item)}
                    className="block w-full text-left text-sm p-2 hover:bg-white rounded border text-gray-700 hover:text-blue-600"
                  >
                    {item.query.street} {item.query.houseNumber}, {item.query.city} {item.query.postalCode}
                    <span className="text-xs text-gray-500 ml-2">
                      {new Date(item.timestamp).toLocaleDateString()}
                    </span>
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      )}
      
      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label htmlFor="street" className="block text-sm font-medium text-gray-700 mb-1">
              Street
            </label>
            <input
              type="text"
              id="street"
              name="street"
              value={formData.street}
              onChange={handleChange}
              onBlur={handleBlur}
              required
              className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                validationErrors.street ? 'border-red-500' : 'border-gray-300'
              }`}
              placeholder="Hauptstraße"
            />
            {validationErrors.street && (
              <p className="text-red-500 text-sm mt-1">{validationErrors.street}</p>
            )}
          </div>
          
          <div>
            <label htmlFor="houseNumber" className="block text-sm font-medium text-gray-700 mb-1">
              House Number
            </label>
            <input
              type="text"
              id="houseNumber"
              name="houseNumber"
              value={formData.houseNumber}
              onChange={handleChange}
              onBlur={handleBlur}
              required
              className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                validationErrors.houseNumber ? 'border-red-500' : 'border-gray-300'
              }`}
              placeholder="123"
            />
            {validationErrors.houseNumber && (
              <p className="text-red-500 text-sm mt-1">{validationErrors.houseNumber}</p>
            )}
          </div>
          
          <div className="relative">
            <label htmlFor="city" className="block text-sm font-medium text-gray-700 mb-1">
              City
            </label>
            <input
              ref={cityInputRef}
              type="text"
              id="city"
              name="city"
              value={formData.city}
              onChange={handleChange}
              onBlur={handleBlur}
              required
              className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                validationErrors.city ? 'border-red-500' : 'border-gray-300'
              }`}
              placeholder="München"
            />
            
            {/* City Suggestions */}
            {showCitySuggestions && (
              <div className="absolute z-10 w-full bg-white border border-gray-300 rounded-md mt-1 max-h-40 overflow-y-auto shadow-lg">
                {citySuggestions.map((suggestion, index) => (
                  <button
                    key={index}
                    type="button"
                    onClick={() => handleCitySelect(suggestion)}
                    className="w-full text-left px-3 py-2 hover:bg-blue-50 focus:bg-blue-50 text-sm"
                  >
                    <div className="font-medium">{suggestion.city}</div>
                    <div className="text-gray-500 text-xs">{suggestion.postalCode} - {suggestion.state}</div>
                  </button>
                ))}
              </div>
            )}
            
            {validationErrors.city && (
              <p className="text-red-500 text-sm mt-1">{validationErrors.city}</p>
            )}
          </div>
          
          <div className="relative">
            <label htmlFor="postalCode" className="block text-sm font-medium text-gray-700 mb-1">
              Postal Code
            </label>
            <input
              ref={postalInputRef}
              type="text"
              id="postalCode"
              name="postalCode"
              value={formData.postalCode}
              onChange={handleChange}
              onBlur={handleBlur}
              required
              className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                validationErrors.postalCode ? 'border-red-500' : 'border-gray-300'
              }`}
              placeholder="80331"
            />
            
            {/* Postal Code Suggestions */}
            {showPostalSuggestions && (
              <div className="absolute z-10 w-full bg-white border border-gray-300 rounded-md mt-1 max-h-40 overflow-y-auto shadow-lg">
                {postalSuggestions.map((suggestion, index) => (
                  <button
                    key={index}
                    type="button"
                    onClick={() => handlePostalCodeSelect(suggestion)}
                    className="w-full text-left px-3 py-2 hover:bg-blue-50 focus:bg-blue-50 text-sm"
                  >
                    <div className="font-medium">{suggestion.postalCode} - {suggestion.city}</div>
                    <div className="text-gray-500 text-xs">{suggestion.state}</div>
                  </button>
                ))}
              </div>
            )}
            
            {validationErrors.postalCode && (
              <p className="text-red-500 text-sm mt-1">{validationErrors.postalCode}</p>
            )}
          </div>
        </div>
        
        <button
          type="submit"
          disabled={isLoading}
          className="w-full bg-blue-600 text-white py-3 px-4 rounded-md hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed font-medium"
        >
          {isLoading ? 'Searching Providers...' : 'Compare Internet Providers'}
        </button>
      </form>
    </div>
  );
};
