import React, { useState, useEffect, useRef } from 'react';
import { SearchQuery } from '../../services/api';
import { searchPostalCodes, getCityByPostalCode, getPostalCodeByCity, AddressSuggestion } from '../../services/addressService';
import { saveLastSearch, getLastSearch, getSearchHistory, SearchHistory } from '../../services/sessionService';

interface SearchFormProps {
  onSearch: (query: SearchQuery) => void;
  isLoading: boolean;
}

interface ValidationErrors {
  street?: string;
  houseNumber?: string;
  city?: string;
  postalCode?: string;
}

export const SearchForm: React.FC<SearchFormProps> = ({ onSearch, isLoading }) => {
  const [formData, setFormData] = useState<SearchQuery>({
    street: '',
    houseNumber: '',
    city: '',
    postalCode: ''
  });

  const [validationErrors, setValidationErrors] = useState<ValidationErrors>({});
  const [showValidation, setShowValidation] = useState(false);
  
  // Address autocompletion states
  const [postalSuggestions, setPostalSuggestions] = useState<AddressSuggestion[]>([]);
  const [citySuggestions, setCitySuggestions] = useState<AddressSuggestion[]>([]);
  const [showPostalSuggestions, setShowPostalSuggestions] = useState(false);
  const [showCitySuggestions, setShowCitySuggestions] = useState(false);
  
  // Session state
  const [searchHistory, setSearchHistory] = useState<SearchHistory[]>([]);
  const [showHistory, setShowHistory] = useState(false);
  
  // Loading progress state
  const [searchProgress, setSearchProgress] = useState(0);
  
  const postalInputRef = useRef<HTMLInputElement>(null);
  const cityInputRef = useRef<HTMLInputElement>(null);

  // Load last search and history on component mount
  useEffect(() => {
    const lastSearch = getLastSearch();
    if (lastSearch) {
      setFormData(lastSearch);
    }
    setSearchHistory(getSearchHistory());
  }, []);

  // Progress animation when searching
  useEffect(() => {
    if (isLoading) {
      setSearchProgress(0);
      const interval = setInterval(() => {
        setSearchProgress(prev => {
          if (prev >= 90) return prev;
          return prev + Math.random() * 15;
        });
      }, 200);
      
      return () => clearInterval(interval);
    } else {
      setSearchProgress(100);
      setTimeout(() => setSearchProgress(0), 1000);
    }
  }, [isLoading]);

  const validateField = (name: string, value: string): string | undefined => {
    switch (name) {
      case 'street':
        if (value && /^\d{5}$/.test(value)) {
          return 'This looks like a postal code. Street names should contain letters.';
        }
        if (value && /^[0-9]+$/.test(value)) {
          return 'Street names should contain letters, not just numbers.';
        }
        break;

      case 'houseNumber':
        if (value && /^[xX]+$/.test(value)) {
          return 'Please enter a valid house number (e.g., 123, 45A).';
        }
        if (value && value.length > 0 && !/^[0-9]+[a-zA-Z]?$/.test(value)) {
          return 'House number should be numeric (e.g., 123, 45A).';
        }
        break;

      case 'city':
        if (value && /^\d{5}$/.test(value)) {
          return 'This looks like a postal code. Did you mean to enter a city name?';
        }
        if (value && /^[0-9]+$/.test(value)) {
          return 'City names should contain letters, not just numbers.';
        }
        if (value && value.length > 0 && !/^[a-zA-ZäöüÄÖÜß\s-]+$/.test(value)) {
          return 'City names should only contain letters and spaces.';
        }
        break;

      case 'postalCode':
        if (value && value.length > 0 && !/^\d{5}$/.test(value)) {
          return 'German postal codes should be exactly 5 digits (e.g., 80331).';
        }
        if (value && /^[a-zA-Z]+$/.test(value)) {
          return 'This looks like a city name. Postal codes should be 5 digits.';
        }
        break;
    }
    return undefined;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setShowValidation(true);

    const errors: ValidationErrors = {};
    Object.keys(formData).forEach(key => {
      const error = validateField(key, formData[key as keyof SearchQuery]);
      if (error) {
        errors[key as keyof ValidationErrors] = error;
      }
    });

    setValidationErrors(errors);

    if (Object.keys(errors).length === 0) {
      saveLastSearch(formData);
      setSearchHistory(getSearchHistory());
      onSearch(formData);
    }
  };

  const handleChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });

    // Address autocompletion with proper async/await
    if (name === 'postalCode' && value.length >= 2) {
      try {
        const suggestions = await searchPostalCodes(value);
        setPostalSuggestions(suggestions);
        setShowPostalSuggestions(suggestions.length > 0);
      } catch (error) {
        console.error('Error fetching postal suggestions:', error);
        setShowPostalSuggestions(false);
      }
    } else if (name === 'postalCode') {
      setShowPostalSuggestions(false);
    }

    if (name === 'city' && value.length >= 2) {
      try {
        const suggestions = await searchPostalCodes(value);
        setCitySuggestions(suggestions);
        setShowCitySuggestions(suggestions.length > 0);
      } catch (error) {
        console.error('Error fetching city suggestions:', error);
        setShowCitySuggestions(false);
      }
    } else if (name === 'city') {
      setShowCitySuggestions(false);
    }

    // Real-time validation
    if (showValidation) {
      const error = validateField(name, value);
      setValidationErrors(prev => ({
        ...prev,
        [name]: error
      }));
    }
  };

  const handlePostalCodeSelect = (suggestion: AddressSuggestion) => {
    setFormData({
      ...formData,
      postalCode: suggestion.postalCode,
      city: suggestion.city
    });
    setShowPostalSuggestions(false);
  };

  const handleCitySelect = (suggestion: AddressSuggestion) => {
    setFormData({
      ...formData,
      city: suggestion.city,
      postalCode: suggestion.postalCode
    });
    setShowCitySuggestions(false);
  };

  const handleHistorySelect = (historyItem: SearchHistory) => {
    setFormData(historyItem.query);
    setShowHistory(false);
  };

  const handleBlur = async (e: React.FocusEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    
    // Auto-fill city/postal code with proper async/await
    if (name === 'postalCode' && value.length === 5) {
      try {
        const city = await getCityByPostalCode(value);
        if (city && !formData.city) {
          setFormData(prev => ({ ...prev, city }));
        }
      } catch (error) {
        console.error('Error auto-filling city:', error);
      }
    }
    
    if (name === 'city' && value && !formData.postalCode) {
      try {
        const postalCode = await getPostalCodeByCity(value);
        if (postalCode) {
          setFormData(prev => ({ ...prev, postalCode }));
        }
      } catch (error) {
        console.error('Error auto-filling postal code:', error);
      }
    }

    // Validation
    const error = validateField(name, value);
    setValidationErrors(prev => ({
      ...prev,
      [name]: error
    }));

    // Hide suggestions after a delay to allow clicks
    setTimeout(() => {
      setShowPostalSuggestions(false);
      setShowCitySuggestions(false);
      setPostalSuggestions([]);
      setCitySuggestions([]);
    }, 200);
  };

  return (
    <div className="max-w-4xl mx-auto p-8 bg-gradient-to-br from-blue-50 to-white rounded-xl shadow-xl border border-blue-100">
      {/* CHECK24-inspired header */}
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-blue-800 bg-clip-text text-transparent mb-3">
          🌐 Internet Provider Comparison
        </h1>
        <p className="text-gray-600 text-lg font-medium">Find the best internet deals in your area</p>
        <div className="mt-2 flex justify-center items-center space-x-4 text-sm text-gray-500">
          <span className="flex items-center">✨ 16,000+ German cities</span>
          <span className="flex items-center">🚀 Real-time comparison</span>
          <span className="flex items-center">🔒 Secure & private</span>
        </div>
      </div>

      {/* Progress indicator during search */}
      {isLoading && (
        <div className="mb-6 bg-gradient-to-r from-blue-50 to-indigo-50 border border-blue-200 rounded-lg p-4">
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center">
              <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-blue-600 mr-3"></div>
              <span className="text-blue-800 font-medium">Comparing 5 providers...</span>
            </div>
            <span className="text-blue-600 font-semibold">{Math.round(searchProgress)}%</span>
          </div>
          <div className="w-full bg-blue-200 rounded-full h-2">
            <div 
              className="bg-gradient-to-r from-blue-500 to-blue-600 h-2 rounded-full transition-all duration-300 ease-out"
              style={{ width: `${searchProgress}%` }}
            ></div>
          </div>
        </div>
      )}

      {/* Search History with improved styling */}
      {searchHistory.length > 0 && (
        <div className="mb-6">
          <button
            type="button"
            onClick={() => setShowHistory(!showHistory)}
            className="inline-flex items-center text-sm text-blue-600 hover:text-blue-800 font-medium hover:underline transition-colors"
          >
            <span className="mr-1">📝</span>
            {showHistory ? 'Hide' : 'Show'} recent searches ({searchHistory.length})
          </button>
          
          {showHistory && (
            <div className="mt-3 bg-gradient-to-r from-gray-50 to-blue-50 rounded-lg p-4 border border-gray-200">
              <div className="space-y-2">
                {searchHistory.map((item, index) => (
                  <button
                    key={index}
                    type="button"
                    onClick={() => handleHistorySelect(item)}
                    className="block w-full text-left text-sm p-3 hover:bg-white rounded-lg border border-gray-200 text-gray-700 hover:text-blue-600 hover:border-blue-300 transition-all duration-200 hover:shadow-md"
                  >
                    <div className="font-medium">
                      {item.query.street} {item.query.houseNumber}, {item.query.city} {item.query.postalCode}
                    </div>
                    <div className="text-xs text-gray-500 mt-1">
                      🕒 {new Date(item.timestamp).toLocaleDateString('de-DE')}
                    </div>
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      )}
      
      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label htmlFor="street" className="block text-sm font-semibold text-gray-700 mb-2">
              🏠 Street
            </label>
            <input
              type="text"
              id="street"
              name="street"
              value={formData.street}
              onChange={handleChange}
              onBlur={handleBlur}
              required
              className={`w-full px-4 py-3 border-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200 ${
                validationErrors.street ? 'border-red-400 bg-red-50' : 'border-gray-300 hover:border-blue-400'
              }`}
              placeholder="Hauptstraße"
            />
            {validationErrors.street && (
              <p className="text-red-500 text-sm mt-2 flex items-center">
                <span className="mr-1">⚠️</span>
                {validationErrors.street}
              </p>
            )}
          </div>
          
          <div>
            <label htmlFor="houseNumber" className="block text-sm font-semibold text-gray-700 mb-2">
              🔢 House Number
            </label>
            <input
              type="text"
              id="houseNumber"
              name="houseNumber"
              value={formData.houseNumber}
              onChange={handleChange}
              onBlur={handleBlur}
              required
              className={`w-full px-4 py-3 border-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200 ${
                validationErrors.houseNumber ? 'border-red-400 bg-red-50' : 'border-gray-300 hover:border-blue-400'
              }`}
              placeholder="123"
            />
            {validationErrors.houseNumber && (
              <p className="text-red-500 text-sm mt-2 flex items-center">
                <span className="mr-1">⚠️</span>
                {validationErrors.houseNumber}
              </p>
            )}
          </div>
          
          <div className="relative">
            <label htmlFor="city" className="block text-sm font-semibold text-gray-700 mb-2">
              🏙️ City
            </label>
            <input
              ref={cityInputRef}
              type="text"
              id="city"
              name="city"
              value={formData.city}
              onChange={handleChange}
              onBlur={handleBlur}
              required
              className={`w-full px-4 py-3 border-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200 ${
                validationErrors.city ? 'border-red-400 bg-red-50' : 'border-gray-300 hover:border-blue-400'
              }`}
              placeholder="München"
            />
            
            {/* Enhanced City Suggestions */}
            {showCitySuggestions && (
              <div className="absolute z-20 w-full bg-white border-2 border-blue-300 rounded-lg mt-1 max-h-48 overflow-y-auto shadow-xl">
                {citySuggestions.map((suggestion, index) => (
                  <button
                    key={index}
                    type="button"
                    onClick={() => handleCitySelect(suggestion)}
                    className="w-full text-left px-4 py-3 hover:bg-blue-50 focus:bg-blue-50 text-sm border-b border-gray-100 last:border-b-0 transition-colors"
                  >
                    <div className="font-medium text-gray-800">{suggestion.city}</div>
                    <div className="text-gray-500 text-xs">{suggestion.postalCode} • {suggestion.state}</div>
                  </button>
                ))}
              </div>
            )}
            
            {validationErrors.city && (
              <p className="text-red-500 text-sm mt-2 flex items-center">
                <span className="mr-1">⚠️</span>
                {validationErrors.city}
              </p>
            )}
          </div>
          
          <div className="relative">
            <label htmlFor="postalCode" className="block text-sm font-semibold text-gray-700 mb-2">
              📮 Postal Code
            </label>
            <input
              ref={postalInputRef}
              type="text"
              id="postalCode"
              name="postalCode"
              value={formData.postalCode}
              onChange={handleChange}
              onBlur={handleBlur}
              required
              className={`w-full px-4 py-3 border-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200 ${
                validationErrors.postalCode ? 'border-red-400 bg-red-50' : 'border-gray-300 hover:border-blue-400'
              }`}
              placeholder="80331"
            />
            
            {/* Enhanced Postal Code Suggestions */}
            {showPostalSuggestions && (
              <div className="absolute z-20 w-full bg-white border-2 border-blue-300 rounded-lg mt-1 max-h-48 overflow-y-auto shadow-xl">
                {postalSuggestions.map((suggestion, index) => (
                  <button
                    key={index}
                    type="button"
                    onClick={() => handlePostalCodeSelect(suggestion)}
                    className="w-full text-left px-4 py-3 hover:bg-blue-50 focus:bg-blue-50 text-sm border-b border-gray-100 last:border-b-0 transition-colors"
                  >
                    <div className="font-medium text-gray-800">{suggestion.postalCode} - {suggestion.city}</div>
                    <div className="text-gray-500 text-xs">{suggestion.state}</div>
                  </button>
                ))}
              </div>
            )}
            
            {validationErrors.postalCode && (
              <p className="text-red-500 text-sm mt-2 flex items-center">
                <span className="mr-1">⚠️</span>
                {validationErrors.postalCode}
              </p>
            )}
          </div>
        </div>
        
        <button
          type="submit"
          disabled={isLoading}
          className="w-full bg-gradient-to-r from-blue-600 to-blue-700 text-white py-4 px-6 rounded-lg hover:from-blue-700 hover:to-blue-800 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed font-semibold text-lg shadow-lg hover:shadow-xl transition-all duration-200 transform hover:scale-[1.02] disabled:transform-none"
        >
          {isLoading ? (
            <span className="flex items-center justify-center">
              <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
              Searching Providers...
            </span>
          ) : (
            <span className="flex items-center justify-center">
              🔍 Compare Internet Providers
            </span>
          )}
        </button>
      </form>
    </div>
  );
};
