import React, { useState } from 'react';
import { SearchForm } from './components/SearchForm/SearchForm';
import { ResultsDisplay } from './components/ResultsDisplay/ResultDisplay';
import { searchProviders, ComparisonResult } from './services/api';

function App() {
  const [results, setResults] = useState<ComparisonResult | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSearch = async (query: any) => {
    setIsLoading(true);
    setError(null);
    
    try {
      const result = await searchProviders(query);
      setResults(result);
    } catch (err: any) {
      setError(err.message || 'Failed to search providers');
    } finally {
      setIsLoading(false);
    }
  };

  const handleShare = (shareId: string) => {
    const shareUrl = `${window.location.origin}/share/${shareId}`;
    navigator.clipboard.writeText(shareUrl);
    alert(`Share link copied to clipboard: ${shareUrl}`);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8">
      <div className="container mx-auto px-4">
        <SearchForm onSearch={handleSearch} isLoading={isLoading} />
        
        {error && (
          <div className="mt-6 p-4 bg-red-100 border border-red-400 text-red-700 rounded">
            Error: {error}
          </div>
        )}
        
        {results && (
          <ResultsDisplay results={results} onShare={handleShare} />
        )}
      </div>
    </div>
  );
}

export default App;
