import React, { useState } from 'react';
import { SearchForm } from './components/SearchForm/SearchForm';
import { searchProviders, ComparisonResult } from './services/api';

function App() {
  const [results, setResults] = useState<ComparisonResult | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSearch = async (query: any) => {
    setIsLoading(true);
    setError(null);
    
    try {
      const result = await searchProviders(query);
      setResults(result);
    } catch (err: any) {
      setError(err.message || 'Failed to search providers');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8">
      <div className="container mx-auto px-4">
        <SearchForm onSearch={handleSearch} isLoading={isLoading} />
        
        {error && (
          <div className="mt-6 p-4 bg-red-100 border border-red-400 text-red-700 rounded">
            Error: {error}
          </div>
        )}
        
        {results && (
          <div className="mt-8 p-6 bg-white rounded-lg shadow-lg">
            <h2 className="text-2xl font-bold mb-4">Search Results</h2>
            <p className="text-gray-600">Found {results.totalOffers} offers from {results.providers.length} providers</p>
            
            <div className="mt-4 space-y-2">
              {results.providers.map((provider) => (
                <div key={provider.provider} className={`p-3 rounded ${provider.success ? 'bg-green-50 border-green-200' : 'bg-red-50 border-red-200'} border`}>
                  <span className="font-medium">{provider.provider}</span>
                  <span className={`ml-2 text-sm ${provider.success ? 'text-green-600' : 'text-red-600'}`}>
                    {provider.success ? `✓ ${provider.offers.length} offers (${provider.responseTime}ms)` : `✗ ${provider.error}`}
                  </span>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
